mapper.insert:

INSERT INTO
t_board (BNO,		SUBJECT,	CONTENT,	WRITER, 	REG_DATE,	READCOUNT)
VALUES	(#{bno},	#{subject}, #{content},	#{writer},	NOW(),		0);

mapper.select:

SELECT 		BNO,		SUBJECT,	CONTENT,	WRITER, 	REG_DATE,	READCOUNT
FROM		t_board
ORDER BY	BNO DESC;

mapper.update:

UPDATE 		t_board
SET			readcount = readcount+1
WHERE		bno=#{bno}

mapper.select:

SELECT 		bno,		subject,	content,	writer, 	reg_date,	readcount
FROM		t_board
WHERE		bno=#{bno}
